import styled from 'styled-components';

export const Container = styled.div`
  .background {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.2);
  }

  .modal {
    position: absolute;
    background-color: #fefefe;
    top: 80px;
    left: 200px;
    right: 200px;
    bottom: 80px;
  }
`;
