import styled from 'styled-components';

export const Container = styled.div`
  height: 100%;
  width: 100%;

  header {
    display: flex;

    img {
      width: 120px;
      height: 120px;
    }

    h1 {
      font-size: 40px;
      margin-left: 20px;
      font-weight: 600;
    }
  }
`;
