import styled from 'styled-components';

export const Container = styled.div`
  height: 100%;
  width: 100%;
  padding: 80px;

  header {
    display: flex;
    align-items: center;

    img {
      width: 120px;
      height: 120px;
      border-radius: 50%;
      border: 1px solid #141414;
    }

    h1 {
      font-size: 50px;
      margin-left: 40px;
      font-weight: 600;
    }
  }

  button {
    position: absolute;
    top: 0;
  }

  p {
    margin-top: 30px;
    font-size: 25px;
  }

  span {
    color: #000;
    margin-top: 20px;
  }
`;
