import styled from 'styled-components';

export const Container = styled.div`
  height: 100%;
  width: 100%;
  padding: 80px;

  header {
    display: flex;
    align-items: center;

    img {
      width: 120px;
      height: 120px;
      border-radius: 50%;
      border: 1px solid #141414;
    }

    h1 {
      font-size: 50px;
      margin-left: 40px;
      font-weight: 600;
    }
  }

  button {
    position: absolute;
    top: -20px;
    right: -20px;
    height: 70px;
    width: 70px;
    border-radius: 50%;
    border: 0;
    background-color: #fe7940;
    font-size: 30px;
    color: #fefefe;
  }

  p {
    margin: 30px 0 20px 0;
    font-size: 25px;
  }

  a, span {
    display: block;
  }

  span {
    margin-bottom: 20px;
  }

  div {
    margin-top: 40px;
    display: flex;
    flex-direction: row;
  }
`;

export const Votebox = styled.div`

`;
